import React, { Component } from 'react';
import './app.scss';
import Header from './components/header';
import Headline from './components/headline';
import Randoms from './components/randomPerson';
import SharedButton from './components/button';
import Certifications from './components/certifications';
import ListItem from './components/listItem';
import { connect } from 'react-redux';
import { Provider } from 'react-redux';
import { store } from './createStore';
import { fetchPosts } from './actions';
import './app.scss';

const tempArr = [
  {
    fName: 'Geo',
    lName: 'Doe',
    email: 'geoDoe123@gmail.com',
    location: 'Dayton',
    onlineStatus: true
  }
];

export class App extends Component {
  constructor(props) {
    super(props);
    this.fetch = this.fetch.bind(this);
  }

  fetch() {
    this.props.fetchPosts();
  }

  render() {
    const { props } = this.props;

    const configButton = {
      buttonText: 'TDD Button- Get Posts',
      emitEvent: this.fetch
    };

    const { data } = this.props;

    const namesList = data.map(name => {
      console.log(name);
    });

    return (
      <div className="App">
        <ul>
          <li className="boy">Name 1</li>
          <li className="girl">Name 2</li>
          <li className="boy">Name 3</li>
          <li className="girl">Name 4</li>
          <li className="boy">Name 5</li>
          <li className="girl">Name 6</li>
          <li className="boy">Name 7</li>
        </ul>

        <Header />
        <SharedButton {...configButton} />
        {/* {posts.length > 0 && (
          <div>
            {posts.map((post, index) => {
              const { title, body } = post;
              const configListItem = {
                title,
                desc: body
              };
              return <ListItem key={index} {...configListItem} />;
            })}
          </div>
        )} */}
        <section className="headline">
          <Headline
            header="Rand-o Pers-O (These people are random)"
            desc="Click 'Next' for random people"
            tempArr={tempArr}
          />
          <Randoms />
        </section>
        <Certifications />
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    posts: state.posts
  };
};

// const ConnectedApp = connect => (mapStateToProps, { fetchPosts })(App);

// export default ConnectedApp(
//   <Provider store={store}>
//     <ConnectedApp />
//   </Provider>
// );

export default connect(
  mapStateToProps,
  { fetchPosts }
)(App);
